    }  

    // Unit (no value)
    public MaybeMonad() {
        // hasValue remains false by default
    }  

    // Map    
    public MaybeMonad Map(Func<int, int> func) {
        if (hasValue) {
            return new MaybeMonad(func(value));
        }
        return this;
    }
}
